mapper.select:
SELECT taskid, title, description, deadline, status, progress, priority, CASE WHEN CURRENT_TIMESTAMP > deadline and status != 3 THEN 1 WHEN CURRENT_TIMESTAMP + INTERVAL '48 hours' > deadline and status != 3 THEN 2 ELSE 0 END AS message FROM task WHERE deleteFlg=0 and loginid='aaa'
and( status = 1 or status = 2 or status = 3 or priority = 1 or priority = 2 or priority = 3 or priority = 4 or 1=0 )
ORDER BY priority DESC,deadline;
