mapper.update:
        UPDATE users
        SET
        loginId=#{newLoginId}
        , createdAt=CURRENT_TIMESTAMP
	where loginId=#{loginId}

        UPDATE users
        SET
        password=#{password}
        , createdAt=CURRENT_TIMESTAMP
	where loginId=#{loginId}

        UPDATE users
        SET
        userName=#{userName}
        , createdAt=CURRENT_TIMESTAMP
	where loginId=#{loginId}
